INSERT INTO `program` (`id_program`, `id_user`, `slug_program`, `judul_program`, `isi_program`, `gambar`, `status_program`, `keywords`, `tanggal_post`, `tanggal`) VALUES
(1, 1, 'kelas-digital', 'Kelas Digital', '<p><em>Kelas digital</em> merupakan suatu proses pembelajaran yang dilaksanakan dengan memanfaatkan teknologi modern. MAN 1 Kota Malang membuat langkah dalam menghadapi zaman salah satunya yaitu dengan membuat kelas digital yang merupakan sebuah Ruang Kelas belajar namun yang berbeda dengan Ruang Kelas pada umumnya ialah Perangkat yang di gunakan untuk Kelas Digital ini adalah Perangkat-perangkat Teknologi Informasi yang bisa mendukung kegiatan belajar mengajar (KBM) serta bisa menjangkau informasi lebih luas lagi dengan sumber yang lebih banyak lagi.</p>', 'kelas-digital1.png', 'Publish', 'digital-class,kelasdigital,kelas-digital,class-digital', '2021-06-24 07:43:52', '2021-07-08 08:37:15'),
(2, 1, 'desain-produk', 'Desain Produk', '<p>Desain Produk adalah rangkaian usaha untuk mempelajari dan merencanakan benda pakai yang fungsional, ergonomis dan estetis sehingga menjadi lebih bernilai dan bermanfaat bagi penggunanya.</p>\r\n<p>MAN 1 Kota Malang mempunyai kegiatan program unggulan yaitu Desain Produk yang melatih siswa siswi lebih mahir dan mandiri dalam berkreasi untuk menghasilkan karya yang mempunyai nilai jual.</p>', 'desain-produk1.png', 'Publish', 'desain produk, desainproduk, product design', '2021-06-21 17:19:29', '2021-07-08 08:37:07'),
(3, 1, 'riset', 'Riset', '<p>Riset atau penelitian sering dideskripsikan sebagai suatu proses investigasi yang dilakukan dengan aktif, tekun, dan sistematis, yang bertujuan untuk menemukan, menginterpretasikan, dan merevisi fakta-fakta. MAN 1 Kota Malang mempunyai program unggulan Riset untuk menunjang peserta didik dalam meraih prestasi baik tingkat kota/kab, provinsi maupun nasional.</p>', 'riset1.png', 'Publish', 'riset, research, kir, karya-ilmiah-remaja', '2021-06-24 04:41:21', '2021-07-08 08:36:53'),
(4, 1, 'tahfidzul-quran', 'Tahfidzul Qur\'an', '<p>MAN 1 Kota Malang memberikan peluang kepada siswa siswi yang ingin memperdalam hafalan Al-Quran dalam program Tahfidzul Qur\'an. MAN 1 Kota Malang juga telah banyak melahirkan para hafaz Qur\'an di setiap lulusannya.</p>', 'tahfid.png', 'Publish', 'Tahfidzul Qur\'an, Tahfidz, Qur\'an, tahfidz-quran', '2021-06-24 04:24:41', '2021-07-08 08:40:48'),
(5, 1, 'pro-it', 'Pro-IT', '<p>Pro-IT / MITC bergerak di bidang Teknologi Informasi yang di dedikasikan kepada siswa siswi untuk mengembangkan bakat minat kemampuan di bidang IT. MAN 1 Kota Malang mempunyai salah satu program unggulan yang kerap muncul dalam perlombaan-perlombaan di tingkat kota/kab, provinsi maupun nasional.</p>', 'pro-it.png', 'Publish', 'IT, it, teknologi-informasi, pro-it, mitc, MITC', '2021-06-24 05:11:40', '2021-06-24 07:54:18'),
(6, 1, 'olimpiade', 'Olimpiade', '<p>Olimpiade menjadi salah satu program unggulan MAN 1 Kota Malang dalam ajang prestasi baik tingkat kota/kab, provinsi, bahkan nasional. MAN 1 Kota Malang telah memberikan wadah kepada siswa siswi yang ingin berkembang dan berpacu dalam bidang prestasi akademik untuk menghasilkan output yang terbaik.</p>', 'olimpiade.png', 'Publish', 'olimpiade, olim, osn, ksn, ksnk, osnk', '2021-06-24 04:25:04', '2021-07-08 08:40:34');

